<?php

namespace App\Mail;

use Illuminate\Bus\Queueable;
use Illuminate\Contracts\Queue\ShouldQueue;
use Illuminate\Mail\Mailable;
use Illuminate\Mail\Mailables\Content;
use Illuminate\Mail\Mailables\Envelope;
use Illuminate\Queue\SerializesModels;

class VoucherMailable extends Mailable
{
    use Queueable, SerializesModels;

    public $reserva;

    public function __construct($reserva)
    {
        $this->reserva = $reserva;
    }

    /**
     * Get the message envelope.
     *
     * @return \Illuminate\Mail\Mailables\Envelope
     */
    public function build()
    {
        $reserva=$this->reserva;
        $facebook="data:image/svg+xml;base64,";
        $instagram="data:image/svg+xml;base64,";
        $tiktok="data:image/svg+xml;base64,";
        $whatsapp="data:image/svg+xml;base64,";
        $ubicacion="data:image/png;base64,";
        $pdf= \PDF::loadView('pages.pdf.ticketpdf',compact('reserva','facebook','instagram','tiktok','whatsapp','ubicacion'))->setPaper('a4');
        $pdfData = $pdf->output();
        $pdfName = $this->reserva->pasajero->nombre . '-' . date("d-m-Y", strtotime($this->reserva->fecha)) . '.pdf';

        return  $this->subject('Voucher Day Expeditions')
        ->attachData($pdfData, $pdfName, ['mime' => 'application/pdf'])
        ->markdown('pages.mails.voucher',[
            'reserva' => $this->reserva
        ]);
    }
}
